INSERT INTO `occupation` (`id`, `isco_group_id`, `concept_type`, `concept_uri`, `preferred_label`, `alt_labels`, `hidden_labels`, `status`, `modified_at`, `regulated_profession_note`, `scope_note`, `definition`, `in_scheme`, `description`, `code`) VALUES
(1, 4, 'Occupation', 'http://data.europa.eu/esco/occupation/0b1d8dcb-d941-4d79-99bf-fc10c1806c43', 'fleet commander', 'deputy chief of naval staff\nnavy commander\ncommander of fleet\nnaval commander\nroyal navy commander', '', 'released', '2016-07-05 16:22:33', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Fleet commanders ensure that naval vessels are ready for inclusion in operations, and are maintained in compliance with rules and regulations. They also supervise naval personnel and are responsible for the operations of the naval service.', '0110.9'),
(2, 14, 'Occupation', 'http://data.europa.eu/esco/occupation/0ee67d5e-8de9-48f0-97c5-ba23d486b1e9', 'secretary of state', 'state secretary\nsenior minister\nunder secretary of state\nminister of state\nundersecretary of state', '', 'released', '2016-07-05 16:18:02', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Secretaries of state assist heads of government departments, such as ministers, and aid in the supervision of proceedings in the department. They assist in the direction of policies, operations, and department staff, and perform planning, resource allocation, and decision-making duties.', '1111.6'),
(3, 15, 'Occupation', 'http://data.europa.eu/esco/occupation/0752ed49-03e7-4d75-8314-a051b3771a1d', 'public administration manager', 'public administration director\npublic institution director\npublic body manager\npublic administrator\ndistrict council manager\npublic service administrator\ncounty council manager\npublic body administrator\npublic service director\nlocal authority officer\nchief of staff\ncivil service administrator\ncity administrator\ngovernment department manager', '', 'released', '2017-01-17 12:11:28', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Public administration managers direct, monitor and evaluate the implementation of government policies. They supervise staff and manage resources used for implementation, and write reports on the implementation process. They also communicate with government officials and the public to inform them on the policies. Public administration managers may also participate in the design and creation of public policies.', '1112.6'),
(4, 19, 'Occupation', 'http://data.europa.eu/esco/occupation/0917ab6b-18a8-4cfd-8e7a-843b88cf9457', 'branch manager', 'regional director\nbranch supervisor\ninternational business manager\nbranch office administrator\nrecruitment agency manager\narea office manager\nsubsidiary manager\nbranch chief\nregional manager\nregional administrative manager', '', 'released', '2017-01-17 13:08:27', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Branch managers are responsible for the management of all the affairs related with a company in a specific geographic region or business branch. They receive indications from the headquarters, and depending on the structure of the company, they aim to implement the strategy of the company while adapting it to the market where the branch operates. They envision management of employees, communications, marketing efforts, and follow up to results and objectives.', '1120.2.1'),
(5, 22, 'Occupation', 'http://data.europa.eu/esco/occupation/04f39bfa-bc03-4480-98bc-b18ce4fe4b4b', 'accounting manager', 'accounting office manager\nfinancial director\ncost accounting manager\naccounting department manager\ngroup accounting manager\nfinancial accounts supervisor\naccounting director\nfinancial accounting manager\naccounts payable manager\nhead of accounts', '', 'released', '2016-07-05 15:24:46', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Accounting managers assume responsibility for all accounting activities relating to financial reporting. They develop and maintain accounting principles and procedures to ensure timely and accurate financial statements issued, supervise accounting staff and manage the accounting activities within the appropriate time frame and budget.', '1211.1.1'),
(6, 25, 'Occupation', 'http://data.europa.eu/esco/occupation/0552ff45-b282-455c-b431-fb05a0e00801', 'foundry manager', 'process manager\nfoundry production manager\nengineering manager\nproduction controller\nfoundry plant manager\nfoundry operations manager', '', 'released', '2017-02-10 17:54:18', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Foundry managers', '1219.5.2'),
(7, 25, 'Occupation', 'http://data.europa.eu/esco/occupation/109e0a5d-203d-4af6-8f70-692111335ec3', 'quality services manager', 'quality service supervisor\nquality service coordinator\nquality assurance supervisor\nquality control process auditor\nquality assurance services manager\nquality control services manager\nquality control manager\nquality assurance coordinator', '', 'released', '2017-01-17 11:48:05', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Quality services managers manage the quality of services in business organisations. They ensure the quality of in-house company operations such as customer requirements and service quality standards. Quality services managers monitor the company\'s performance and implement changes where necessary.', '1219.7'),
(8, 25, 'Occupation', 'http://data.europa.eu/esco/occupation/1313aeea-63a9-4b4d-82d1-dbe00ff9ed10', 'corporate social responsibility manager', 'sustainable services manager\nsustainability project manager\ncorporate responsibility director\ncorporate responsibility manager\nchief sustainability officer\ncorporate sustainability director', '', 'released', '2017-02-23 12:13:17', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Corporate social responsibility managers monitor the practices of organisations and companies with regard to ethics and impact on the larger community. They advise on social responsibility and sustainability matters depending on the company\'s needs. Corporate social responsibility managers promote actions that are environmentally conscious, philanthropic or related to human rights.', '1219.2'),
(9, 27, 'Occupation', 'http://data.europa.eu/esco/occupation/04b52c04-0620-4558-8f89-e252de2963ff', 'auction house manager', 'auctioneer\nvaluer\nauction house director\nauction house administrator', '', 'released', '2017-02-23 12:45:52', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Auction house managers are responsible for the staff and activities in an auction house. Moreover, they manage the finances and marketing aspects of the auction house.', '1221.1'),
(10, 27, 'Occupation', 'http://data.europa.eu/esco/occupation/0ddbf393-38f1-4c22-b14c-fe775008f321', 'chief marketing officer', 'chief commercial strategist\nhead of marketing\ndirector of marketing\nsenior marketing officer\nchief commercial officer\nchief commercial strategy officer', '', 'released', '2016-07-05 15:24:07', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Chief marketing officers manage the high level marketing operations in a company. They coordinate all the efforts related to the marketing, promotional, and advertising activities across units or geographical areas. They make sure that different activities aimed at creating awareness of products are profitable for the company. They take decisions and prepare reports on marketing projects and the costs they entail.', '1221.3'),
(11, 29, 'Occupation', 'http://data.europa.eu/esco/occupation/01bcac9f-7881-4664-bd63-dea700e3b117', 'clothing development manager', 'clothing manager\nsample room manager\nclothing product manager\napparel product developer\nclothes product developer', '', 'released', '2016-07-05 17:13:21', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Clothing development managers define product concepts that are consistent with target consumer and overall marketing strategy. They receive scientific findings and specifications in order to lead the briefing and implementation of all relevant seasonal and strategic concepts, including distribution by channel, product, colour introductions, and merchandised assortments. They ensure realisation and execution within budget. They manage and execute the product line and category life cycle from concept determination through sales and distribution, contribution in research of market, industry trends to influence category concepts and products.', '1223.2.1.1'),
(12, 29, 'Occupation', 'http://data.europa.eu/esco/occupation/1488589a-7b83-471d-b094-492f5616f4e0', 'product development manager', 'research and development manager\nproduct designer\nproduct development specialist\ninnovation department manager\nproduct development executive', '', 'released', '2017-02-10 18:22:26', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Product development managers coordinate the development of new products from beginning to end. They receive briefings and start envisioning the new product considering design, technical and cost criteria. They conduct research on market needs and create prototypes of new products for untapped market opportunities. Product development managers also improve and boost technological quality.', '1223.2.1'),
(13, 35, 'Occupation', 'http://data.europa.eu/esco/occupation/02f26b2f-879c-4811-8381-65e8dd3f4a16', 'footwear quality manager', 'footwear quality executive\nfootwear quality assurance manager\nfootwear quality control manager\nfootwear quality coordinator', '', 'released', '2016-07-05 17:15:12', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Footwear quality managers implement, manage and promote the quality systems in the company, using adequate tools and methodologies based on national, international or company standards. They are in charge of establishing requirements and objectives, as well as preparing documents. They analyse complaints, and promote and coordinate corrective and preventive measures. They promote an effective internal and external communication and ensure the consumer\'s demands are met. They are responsible for the definition of instruments which monitor and control of the quality systems, namely internal or external auditing, and they participate in the internal auditing.', '1321.1.2.1'),
(14, 35, 'Occupation', 'http://data.europa.eu/esco/occupation/0368c4d4-94cf-4168-a84d-283a25880e0d', 'metal production manager', 'metal development managers\noperations manager\nmetal engineer\nmetal development engineers\nplant manager\nmetal development experts\nsite manager\nmetal production manager\nmetal planning engineer\ndevelopment engineer\nfacility manager', '', 'released', '2017-01-17 13:24:28', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Metal production managers organise and manage the day-to-day and long-term project work in a metal fabrication factory, to process basic metals into fabricated metals. They create and schedule production plans, recruit new staff, enforce safety and company policies, and strive for customer satisfaction through guaranteeing the product\'s quality.\\\n', '1321.1.1.8'),
(15, 35, 'Occupation', 'http://data.europa.eu/esco/occupation/0d21a30d-25d9-427b-b8e4-339408425ff5', 'chemical production manager', 'chemical production director\nchemical operations manager\nchemical production coordinator\nchemical manufacturing manager\nchemical production quality supervisor\nchemical manufacturing director\nchemical process manager\nproduction manager of chemicals\noperations manager chemical production\nchemical process engineer\nchemical production engineer\nchemical production planner', '', 'released', '2016-07-05 16:57:10', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Chemical production managers are responsible for the technical coordination and control of the chemical production processes. They steer one or more manufacturing units and oversee the implementation of technical and human means, within the framework of objectives of volume, quality and planning. Chemical production managers design and ensure that the production plans and schedules are met. They are responsible for implementation of the processes designed to ensure quality of the manufactured product, good working conditions and environmental practices, and safety of the workplace.', '1321.1.1.1'),
(16, 36, 'Occupation', 'http://data.europa.eu/esco/occupation/0633ec3f-91f1-4038-bc29-e0ec4cd7f1e9', 'oil and gas production manager', 'oil production manager\noilfield production manager\noil and gas production scheduler\noil and gas production project manager\noil field production manager\noil production scheduler\ngas operations manager\ngasfield production manager\noil production project manager\ngas field production manager\ngas production scheduler\noil and gas operations manager\noil operations manager\ngas production project manager\ngas production manager', '', 'released', '2017-02-10 17:55:27', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Oil and gas production managers coordinate and implement short and medium term oil and gas production schedules and plans, such as drilling, extraction operations and waste management. They steer one or more units and oversee the implementation of technical and human means, within the framework of objectives of volume, quality and planning. Oil and gas production managers design and ensure that the production plans and schedules are met.', '1322.1.2'),
(17, 36, 'Occupation', 'http://data.europa.eu/esco/occupation/107d21c6-973b-411c-89fd-69da48d29ada', 'mine manager', 'mining operations manager\nmine supervisor\nmine operations manager\nresident manager\nfacility manager\nquarry manager\nmining engineer\nsite manager\nmine engineer\nplant manager\noperations manager', '', 'released', '2017-01-17 14:02:54', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Mine managers control, direct, plan and coordinate mining production activities. They have statutory responsibility for safety and are also responsible for environmental impact. They oversee the acquisition, installation, maintenance and storage', '1322.1'),
(18, 38, 'Occupation', 'http://data.europa.eu/esco/occupation/00fdad73-4551-4e43-b256-1d4a3759db1e', 'import export manager in meat and meat products', 'graduate export manager in meat and meat products\nsenior export manager in meat and meat products\ninternational trade compliance manager in meat and meat products\ninternational trade manager in meat and meat products\nsenior import manager in meat and meat products\nassistant export manager in meat and meat products\ntrainee import manager in meat and meat products\ngraduate import manager in meat and meat products\ngraduate import export manager in meat and meat products\nassistant import export manager in meat and meat products\nassistant import manager in meat and meat products\nsenior import export manager in meat and meat products\ncustoms compliance manager in meat and meat products\ntrainee export manager in meat and meat products\ntrainee import export manager in meat and meat products', '', 'released', '2016-07-05 17:29:24', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Import export managers in meat and meat products install and maintain procedures for cross-border business, coordinating internal and external parties.', '1324.3.2.22'),
(19, 38, 'Occupation', 'http://data.europa.eu/esco/occupation/032d4115-03ec-49c9-853d-678f8a41969b', 'electronic and telecommunications equipment and parts distribution manager', 'electronic and telecommunications equipment and parts distribution planner\nelectronic and telecommunications equipment and parts supply chain manager\nsenior electronic and telecommunications equipment and parts distribution manager\ngraduate electronic and telecommunications equipment and parts distribution manager\nelectronic and telecommunications equipment and parts logistics manager\nelectronic and telecommunications equipment and parts supply chain planner\nassistant electronic and telecommunications equipment and parts distribution manager\ntrainee electronic and telecommunications equipment and parts distribution manager\nelectronic and telecommunications equipment and parts logistics planner', '', 'released', '2016-12-22 11:32:55', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Electronic and telecommunications equipment and parts distribution managers plan the distribution of electronic and telecommunications equipment and parts to various points of sales.', '1324.3.1.6.11'),
(20, 38, 'Occupation', 'http://data.europa.eu/esco/occupation/0cbfa809-2d71-44e5-b736-c4da3c8fe09d', 'import export manager in agricultural machinery and equipment', 'assistant export manager in agricultural machinery and equipment\nassistant import manager in agricultural machinery and equipment\nassistant import export manager in agricultural machinery and equipment\nsenior import export manager in agricultural machinery and equipment\nsenior export manager in agricultural machinery and equipment\ngraduate import manager in agricultural machinery and equipment\ntrainee import manager in agricultural machinery and equipment\ntrainee export manager in agricultural machinery and equipment\ninternational trade compliance manager in agricultural machinery and equipment\ngraduate export manager in agricultural machinery and equipment\ntrainee import export manager in agricultural machinery and equipment\ngraduate import export manager in agricultural machinery and equipment\nsenior import manager in agricultural machinery and equipment\ncustoms compliance manager in agricultural machinery and equipment\ninternational trade manager in agricultural machinery and equipment', '', 'released', '2016-07-05 17:28:19', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Import export managers in agricultural machinery and equipment install and maintain procedures for cross-border business, coordinating internal and external parties.', '1324.3.2.1'),
(21, 38, 'Occupation', 'http://data.europa.eu/esco/occupation/0d442b70-5413-4820-bebf-36dd5ce437ac', 'air traffic manager', 'air traffic operations supervisor\nair traffic supervisor\nair traffic control manager\nair traffic senior manager\nair navigation manager\ncentre director for air traffic systems and operations\nflight navigation manager\nair traffic operations manager\nflight control manager', '', 'released', '2017-02-23 12:03:08', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Air traffic managers coordinate the planning of ground control, the maintenance of the aircrafts, and the handling of customers. They strive for the most efficient use of resources in directing the aircraft. They manage safety, quality, and risks in everyday work. They also plan and compare performance with other air navigation service providers.', '1324.2'),
(22, 38, 'Occupation', 'http://data.europa.eu/esco/occupation/12142c2c-9b6f-4e4f-8153-f1a51554e4e0', 'import export manager in machinery, industrial equipment, ships and aircraft', 'trainee export manager in machinery, industrial equipment, ships and aircraft\nassistant export manager in machinery, industrial equipment, ships and aircraft\ntrainee import export manager in machinery, industrial equipment, ships and aircraft\ntrainee import manager in machinery, industrial equipment, ships and aircraft\ninternational trade manager in machinery, industrial equipment, ships and aircraft\ninternational trade compliance manager in machinery, industrial equipment, ships and aircraft\ngraduate import export manager in machinery, industrial equipment, ships and aircraft\nsenior import manager in machinery, industrial equipment, ships and aircraft\nassistant import manager in machinery, industrial equipment, ships and aircraft\nsenior import export manager in machinery, industrial equipment, ships and aircraft\nassistant import export manager in machinery, industrial equipment, ships and aircraft\nsenior export manager in machinery, industrial equipment, ships and aircraft\ncustoms compliance manager in machinery, industrial equipment, ships and aircraft\ngraduate export manager in machinery, industrial equipment, ships and aircraft\ngraduate import manager in machinery, industrial equipment, ships and aircraft', '', 'released', '2016-07-05 17:29:34', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Import export managers in machinery, industrial equipment, ships and aircraft install and maintain procedures for cross-border business, coordinating internal and external parties.', '1324.3.2.20'),
(23, 38, 'Occupation', 'http://data.europa.eu/esco/occupation/13b12b2d-e223-46f5-a6c0-cb77fe37f03a', 'textile industry machinery distribution manager', 'textile industry machinery supply chain planner\ngraduate textile industry machinery distribution manager\ntextile industry machinery distribution planner\nassistant textile industry machinery distribution manager\ntextile industry machinery logistics planner\ntrainee textile industry machinery distribution manager\nsenior textile industry machinery distribution manager\ntextile industry machinery supply chain manager\ntextile industry machinery logistics manager', '', 'released', '2016-12-22 11:20:45', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Textile industry machinery distribution managers plan the distribution of textile industry machinery to various points of sales.', '1324.3.1.6.27'),
(24, 46, 'Occupation', 'http://data.europa.eu/esco/occupation/01649ae9-22ee-4dba-83a8-3a2c51fbbe95', 'head of higher education institutions', 'university chancellor\nrector of higher education\nrector for higher education institutions', '', 'released', '2016-10-20 15:41:50', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Heads of higher education institutions manage the day-to-day activities of a higher education institution, such as a college or vocational school. Heads of higher education institutions make decisions concerning admissions and are responsible for meeting curriculum standards, which facilitate academic development for the students. They manage staff, the school\'s budget, campus programmes and oversee the communication between departments. They also ensure the institution meets the national education requirements set by law.', '1345.1.5'),
(25, 46, 'Occupation', 'http://data.europa.eu/esco/occupation/13e30c08-8834-4e4f-b6f9-89fffed4e310', 'special educational needs head teacher', 'director of special educational needs school\nspecial educational needs headmaster\nspecial educational needs principal\nspecial educational needs headteacher\ninclusive education principal\nhead teacher of special educational needs school\nheadteacher of special educational needs school\nSEN head teacher\nhead teacher in special educational needs\nspecial educational needs school director', '', 'released', '2016-12-22 21:32:16', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Special educational needs head teachers manage the day-to-day activities of a special education school. They supervise and support staff, as well as research and introduce programs that provide the necessary assistance for students with physical, mental or learning disabilities. They may make decisions concerning admissions, are responsible for meeting curriculum standards and ensure the school meets the national education requirements set by law. Special educational needs head teachers also manage the school\'s budget and are responsible for maximising the reception of subsidies and grants. They also review and adopt their policies in accordance to current research conducted in the special needs assessment field.', '1345.1.9'),
(26, 51, 'Occupation', 'http://data.europa.eu/esco/occupation/02e19186-458f-40eb-9e78-31c656d12ac7', 'rooms division manager', 'director of rooms\nfront desk manager\nrooms and reservations manager\nreservations manager\nreception manager\nfront office manager\nrooms manager\nhead receptionist', '', 'released', '2020-06-10 16:11:25', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Rooms division managers are in charge of managing and coordinating a team of employees at front desk, reservations, housekeeping and maintenance departments.\n', '1411.2'),
(27, 54, 'Occupation', 'http://data.europa.eu/esco/occupation/00913533-5237-4870-9003-d1cb806603c9', 'tobacco shop manager', 'smokers products shop manager\ntobacconists shop manager\ncigar and cigarette store manager\ntobacco store manager\ncigarette shop manager\ncigar shop manager\ncigarette store manager\ncigar and cigarette shop manager\npurveyor of tobacco and tobacco products\ntobacconist store manager\ncigar store manager\ntobacconists store manager\npipe smokers shop manager\ntobacconist shop manager', '', 'released', '2016-07-05 17:19:23', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Tobacco shop managers assume responsibility for activities and staff in specialised shops.', '1420.4.42'),
(28, 54, 'Occupation', 'http://data.europa.eu/esco/occupation/0249b723-1d20-4ff9-8623-d20b98d0b4a4', 'shoe and leather accessories shop manager', 'footwear store manager\ncobblers store manager\nshop manager (footwear and leather goods retail store)\nleather accessories shop manager\nshoe shop manager\nshoe and leather accessories store manager\nshoe store manager\nleather accessories store manager\ncobblers shop manager', '', 'released', '2016-07-05 17:16:48', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Shoe and leather accessories shop managers assume responsibility for activities and staff in specialised shops.', '1420.4.38'),
(29, 54, 'Occupation', 'http://data.europa.eu/esco/occupation/03b55cb5-9735-4582-8e20-2b1cb76635cb', 'audiology equipment shop manager', 'audiology shop manager\nhearing aid shop manager\naudiology retail service manager\naudiology centre manager\nretail audiologist\naudiology equipment retail  manager\naudiology sales manager\nhearing help shop manager', '', 'released', '2016-07-05 17:18:44', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', '', '1420.4.4'),
(30, 54, 'Occupation', 'http://data.europa.eu/esco/occupation/0c849f7b-fa3b-4faf-8501-0e50cd200ba1', 'telecommunication equipment shop manager', 'phone store manager\nmobile phone and devices shop manager\nmobile phone shop manager\nmobile phone store manager\nmobile devices and accessories shop manager\nmobile phone and accessories shop manager\nphone shop manager\nmobile phone and devices store manager\nmobile devices and accessories store manager\nmobile phone and accessories store manager', '', 'released', '2016-07-05 17:17:09', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Telecommunication equipment shop managers assume responsibility for activities and staff in specialised shops.', '1420.4.40'),
(31, 56, 'Occupation', 'http://data.europa.eu/esco/occupation/03632d98-0ae3-4dd2-941c-3b48de9a0219', 'performance production manager', 'live event production manager\ntheatre production manager\ntheater production manager\nfilm production manager', '', 'released', '2016-12-21 10:06:17', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Performance production managers take care of a range of practical issues concerning the production. They deal with matters ranging from the recruitment of staff, procurement of materials and services, to', '1431.1.2'),
(32, 56, 'Occupation', 'http://data.europa.eu/esco/occupation/080fc550-b7ca-4576-aaf5-06fffbede994', 'spa manager', 'wellness manager\nspa and wellness manager\nspa director\nspa managers\nmanager of spa', '', 'released', '2017-01-17 14:05:36', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Spa managers coordinate the day-to-day operations of the spa establishment in order to provide guests with the best customer experiences. They oversee the activities and performance of staff, manage the financial aspects of the Spa, deal with suppliers and run advertising campaigns for the spa in order to attract more customers.', '1431.2.5'),
(33, 56, 'Occupation', 'http://data.europa.eu/esco/occupation/146f2c99-3f8b-4856-95ba-3ecfea681809', 'gambling manager', 'gambling general manager\ngambling managing director\ngambling facility general manager\ngambling facility managing director\ngambling chief executive officer\ngambling facility chief executive officer\ngambling facility manager\ngeneral manager', '', 'released', '2017-02-06 12:32:12', 'http://data.europa.eu/esco/regulated-professions/unregulated', 'Includes <a resource=\"http://data.europa.eu/esco/occupation/146f2c99-3f8b-4856-95ba-3ecfea681809\" typeOf=\"http://data.europa.eu/esco/model#Occupation\" href=\"http://data.europa.eu/esco/occupation/146f2c99-3f8b-4856-95ba-3ecfea681809\"><span property=\"http:/', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Gambling managers organise and coordinate the activities of a gambling facility. They oversee daily operations and facilitate communications between staff and customers. They manage and train staff and strive to improve the profitability of their business. They take responsibility for all gambling activities and ensure that relevant gambling rules and regulations are followed.', '1431.2.3'),
(34, 62, 'Occupation', 'http://data.europa.eu/esco/occupation/0809ea8a-4f77-4351-a7f1-b9a7b99b6c0b', 'weather forecaster', 'weather forecast anchor\nmeteorology science researcher\nmedia weather forecaster\nsenior weather forecaster\nmeteorology research analyst\nweather anchor\nmeteorology scientist\nmeteorology analyst\nnumerical weather forecaster\non-line weather forecaster\nmeteorology researcher\nweather presenter\noperational weather forecaster\nweather forecast presenter\nweather forecasters', '', 'released', '2016-07-05 13:45:58', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Weather forecasters gather meteorological data. They predict the weather according to these data. Weather forecasters present these forecasts to the audience via radio, television or online.', '2112.1.3'),
(35, 63, 'Occupation', 'http://data.europa.eu/esco/occupation/0d93706d-32fd-4de3-aa08-be1003e325da', 'chemist', 'toxicologist\nchemical researcher\norganic chemist\ninorganic chemist\npharmacologist\nchemical engineer\nforensic chemist\nresearch chemist\nenvironmental chemist\nmanufacturing chemist\nquality control chemist\nindustrial chemist\nbiochemist\nproduction chemist\nchemistry technician\nquality assurance chemist\nnuclear chemist\nchemical analyst', '', 'released', '2017-01-17 13:14:05', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Chemists perform laboratory research by testing and analysing the chemical structure of substances.They translate the research results into industrial production processes which are further used in the development or improvement of products. Chemists are also testing the quality of the manufactured products and their environmental impact.', '2113.1'),
(36, 63, 'Occupation', 'http://data.europa.eu/esco/occupation/11b6390b-1e48-4903-a5df-d3e04b451a19', 'cosmetic chemist', 'formulation development chemist\ncosmetics formulation chemist\nresearch chemist\ncosmetics formulator\ncosmetics formulations chemist\ncosmetics formulation development chemist\ncosmetics development formulator\nskin care formulation chemist\ncosmetics research chemist\ncosmetics development chemist', '', 'released', '2016-11-29 17:18:06', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Cosmetic chemists develop formulas in order to create and test new cosmetic products and improve existing cosmetic products such as perfumes and fragrances, lipstick, waterproof lotions and makeup, hair dye, soaps and detergents with special properties, topical medicines or health supplements.', '2113.1.4'),
(37, 66, 'Occupation', 'http://data.europa.eu/esco/occupation/068c2787-58ff-468e-bbb6-c4e5dd6ee482', 'actuarial consultant', 'actuaries\nactuarial analyst\nactuarial clerks\nactuarial analysts\nhealth actuary\nactuarial adviser\nactuarial advisor\ninsurance actuaries\nactuarial mathematicians\nhealth actuaries\nactuarial clerk\nactuarial mathematician\nconsulting actuary\ninsurance actuary\nactuarial advisors\nconsulting actuaries\nactuary', '', 'released', '2016-07-05 15:24:23', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Actuarial consultants analyse, manage and provide guidance on financial impact of risks. They can work in fields related to insurance, pension, investment, banking, healthcare etc. Actuarial consultants apply technical and statistical models and theories to give strategic, commercial, and financial advice.', '2120.1'),
(38, 68, 'Occupation', 'http://data.europa.eu/esco/occupation/10291962-c0c7-4e5b-a7ed-0762b4d9b010', 'pharmacologist', 'pharmacology analyst\npharmacology scientist\npharmacology research analyst\npharmacology science researcher\npharmacology studies analyst\npharmacology studies research analyst\npharmacology researcher\npharmacology studies scientist\npharmacology studies researcher\npharmacology research scientist\npharmacology scholar\npharmacology studies research scientist\npharmacology studies scholar\npharmacologists', '', 'released', '2017-01-17 14:19:09', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Pharmacologists study the manner in which drugs and medicaments interact with organisms, living systems, and their parts (i.e. cells, tissues, or organs). Their research aims at identifying substances that can be ingested by humans and that exert adequate biochemical functions for curing illnesses.', '2131.7'),
(39, 70, 'Occupation', 'http://data.europa.eu/esco/occupation/11d72bd6-779a-4643-9962-313833740094', 'environmental scientist', 'green infrastructure analyst\nenvironmental researcher\nland survey technician\ngeologist\ngeological technician\nenvironmental impact expert\nregulatory compliance environmental professional\nenvironmental surveyor\nenvironmental scientist advisor\nenvironmental chemist\ngeological technologist\nenvironmental field scientist\nenvironmental engineer\nsoil scientist\necosystem restoration project manager\nmineral technician\nmineral technologist\nfield scientist', '', 'released', '2017-01-17 12:26:23', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Environmental scientists identify problems and find solutions in order to minimize environmental hazards by performing analysis', '2133.7'),
(40, 75, 'Occupation', 'http://data.europa.eu/esco/occupation/0ba95ace-bdf5-4ee6-92af-32e1ec9c0de6', 'steam engineer', 'steam engineering adviser\npower steam engineer\nsteam engineering consultant\nsteam technology engineering expert\nsteam engineers\nsteam technology engineer\nsteam engineering expert\nsteam technology engineering consultant\nsteam technology engineering specialist\nsteam engineering specialist\nsteam technology engineering adviser\nsteam power engineer', '', 'released', '2016-07-05 16:03:30', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Steam engineers provide energy and utilities to facilities, such as steam, heat and refrigeration. They operate and maintain equipment such as boilers and air compressors, and research and develop new methods and improvements for the provision of utilities.', '2144.1.20'),
(41, 77, 'Occupation', 'http://data.europa.eu/esco/occupation/03b2f112-a891-461d-a8d4-effb9dcf2df6', 'quarry engineer', 'drilling & blasting engineer\nmine engineer\ndrilling and blasting engineer\nmine planning engineer\nexplosives engineer\ndrilling and blasting supervisor\ndrill and blast engineer\nquarry manager\nmining engineer\ndrill & blast engineer', '', 'released', '2017-01-17 12:47:14', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Quarry engineers analyse which extraction methods such as excavating, drilling and blasting are most suited to extract raw materials from the ground. They develop plans before a new quarry is opened, assessing if the quarry is profitable. Quarry engineers manage the everyday operations in a quarry, create and maintain progress reports, oversee the staff, ensure health and safety and assess the environmental impact a quarry has on its environment.', '2146.11'),
(42, 78, 'Occupation', 'http://data.europa.eu/esco/occupation/01989dd8-36c0-43cc-a82d-2bfc409de9c4', 'dismantling engineer', 'disassembly planner\ndismantling manager\ndismantling planner', '', 'released', '2016-07-05 16:23:14', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Dismantling engineers research and plan the optimal way to dismantle industrial equipment, machinery and buildings that reached the end-of-life phase. They analyse the required work and schedule the various operations. They give team leaders instructions and supervise their work. \n', '2149.6'),
(43, 78, 'Occupation', 'http://data.europa.eu/esco/occupation/01ffb917-98dc-48c1-91ad-93c4104e791d', 'biomedical engineer', 'BME consultant\nbio-medical engineer\nbiomedical engineering adviser\nbiomedical technology engineering expert\nBME adviser\nbiomedical engineering expert\nBME expert\nbiomedical engineering consultant\nbiomedical technology engineering specialist\nbiomedical technology engineer\nbiomedical engineering specialist\nbiomedical technology engineering adviser\nBME specialist\nbiomedical technology engineering consultant', '', 'released', '2016-07-05 16:16:38', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Biomedical engineers combine knowledge of engineering principles and biological findings for the development of medical treatments, medicaments, and general healthcare purposes. They can develop solutions ranging from the improvement of the components in conventional medicaments up to implants developments, and tissue treatment.', '2149.4.1'),
(44, 78, 'Occupation', 'http://data.europa.eu/esco/occupation/0cca32c2-9308-4927-adb2-14771ab787f0', 'quantity surveyor', 'QS\nquntity surveyor\nquantity survey professional\nquantity surveying consultant\nquantities surveyor\nconstruction project cost consultant\nquantity surveior\nconstruction cost consultant\ndevelopment project cost consultant\ngraduate quantity surveyor\nchartered quantity surveyor\nquantity survey specialist\nassistant quantity surveyor\nquantity survey expert\nconstruction project manager\nquantity survey consultant\nbuilding estimator\ndevelopment project cost manager', '', 'released', '2017-02-24 09:37:36', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Quantity surveyors have under their helm complete management of the cost involved in building and construction projects from the inception of the project until the delivery. They strive for an efficient use of the resources whilst keeping an eye on quality, quality standards, and client\'s requirements.', '2149.12'),
(45, 78, 'Occupation', 'http://data.europa.eu/esco/occupation/0e99c929-364f-4b0a-8a64-2aab42420f00', 'renewable energy engineer', 'renewables engineer\nproject engineer, PV array\nproject engineer, renewables\nrenewable energy technology engineering specialist\nrenewable energy engineering specialist\nrenewable energy technology engineer\nrenewable energy systems engineer\nasset engineer, renewable power\nproject engineer, offshore wind\nrenewable energy engineering adviser\nrenewable power engineer\nrenewable energy technology engineering adviser\nturbine engineer\nrenewable energy engineering expert\nrenewable energy engineering consultant\nrenewable energy technology engineering expert\nindustrial energy engineer\nwind turbine project engineer\nrenewable energies engineer\nrenewable energy technology engineering consultant\nproject engineer, energy efficiency', '', 'released', '2017-01-13 11:40:31', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Renewable energy engineers research alternative sources of energy in order to design systems for renewable energy production. They strive to optimise energy production from renewable sources, and reduce production expenses and environmental strain. They design systems which focus on energy sustainability and efficiency.', '2149.7.3'),
(46, 81, 'Occupation', 'http://data.europa.eu/esco/occupation/0c7c9606-fde3-4658-8136-8fe0c9b8bd09', 'medical device engineer', 'engineer in biomedical systems\nengineer of medical devices\nbiomedical systems developer\nengineer of medical instruments\nmedical device developer\nbiomedical systems engineer\northopaedic engineer\naudiology engineer\nmedical technology engineer\nmedical-technical equipment engineer\nmedical-technical test engineer\nmedical instrument engineer\nclinical engineer\nbiomedical engineer\nmedical-technical device engineer\nengineer of medical technology', '', 'released', '2017-02-02 12:26:26', 'http://data.europa.eu/esco/regulated-professions/unregulated', 'Includes people working in hospitals. \n Includes people performing technical management and ensuring operational readiness of medical equipment in use.', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Medical device engineers design and develop medical-technical systems, installations, and equipment such as pacemakers, MRI scanners, and X-ray machines. They monitor the whole manufacturing process from concept design to product implementation. activities undertaken include, among others, designing product improvements, developing methods and techniques to evaluate design suitability, coordinating initial production, developing test procedures, and designing manufacturing diagrams.', '2152.1.5'),
(47, 82, 'Occupation', 'http://data.europa.eu/esco/occupation/02eb0ae6-ecdd-4602-9c8e-60ffe6dbe1e2', 'telecommunications engineer', 'broadcast engineer\ntelecommunications designer\ntelecommunications technologist\ncommunications designer\ntelecommunications engineers\ncommunications engineer\ntransmission engineer\ntelecoms engineer\ntelecom engineer\ninformation systems engineer\nradio engineer', '', 'released', '2017-01-26 10:46:39', 'http://data.europa.eu/esco/regulated-professions/unregulated', 'Excludes people performing managerial activities.', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Telecommunications engineers design, build, test and maintain telecommunication systems and networks, including radio and broadcasting equipment. \\\nThey analyse customer needs and requirements, ensure that the equipment meets regulations, and prepare and present reports and proposals on telecommunication-related problems. Telecommunications engineers design and oversee the service delivery in all it\'s phases, supervising the installation and use of telecommunications equipment and facilities, preparing documentation and providing training for company staff once new equipment has been installed.', '2153.1');
INSERT INTO `occupation` (`id`, `isco_group_id`, `concept_type`, `concept_uri`, `preferred_label`, `alt_labels`, `hidden_labels`, `status`, `modified_at`, `regulated_profession_note`, `scope_note`, `definition`, `in_scheme`, `description`, `code`) VALUES
(48, 86, 'Occupation', 'http://data.europa.eu/esco/occupation/06f89f2c-c6e9-40c5-a4a5-0e34d5fbc184', 'footwear designer', 'shoe designer\nfootwear CAD designer\nwomen\'s footwear designer\nmen\'s footwear designer\nfootwear technologist\nfootwear CAD technical designer\nfootwear stylist\nfootwear fashion designer\nfootwear designer and developer\nsports footwear designer\nfootwear designer & developer\nshoe stylist\nchildren\'s footwear designer\nfashion footwear designer', '', 'released', '2017-01-17 13:48:41', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Footwear designers perform fashion trends analysis, forecasting and market research, create footwear concepts and build collection lines by operating mood or concept boards, colour palettes, materials, drawings and sketches etc. They conduct the sampling process, make footwear prototypes and samples for presentations aimed to promoting the footwear concepts and collections. They identify the range of materials and components, define the design specifications by collaborating with the technical team and review the footwear samples, prototypes, and collections.', '2163.1.3.2'),
(49, 86, 'Occupation', 'http://data.europa.eu/esco/occupation/07c2287f-3caa-4128-b7c8-59801084d931', 'puppet designer', 'puppet designer & builder\npuppet designer and builder\npuppet builder\npuppet designer & maker\npuppet designer/maker\npuppet designer and maker\npuppet maker\npuppet creator', '', 'released', '2017-01-26 12:52:46', 'http://data.europa.eu/esco/regulated-professions/unregulated', 'Excludes <a resource=\"http://data.europa.eu/esco/occupation/2800e788-57b4-49f7-9c00-6c35e12cfb8e\" typeOf=\"http://data.europa.eu/esco/model#Occupation\" href=\"http://data.europa.eu/esco/occupation/2800e788-57b4-49f7-9c00-6c35e12cfb8e\"><span property=\"http:/', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Puppet designers design and create puppets and manipulable objects for performers. Their work is based on research and artistic vision. Their design is influenced by and influences other designs and must be conform with these designs and the overall artistic vision. Therefore, the designers work closely with artistic directors, operators and the artistic team. Puppet designers make puppets and manipulable objects out of a variety of materials, and may build robotic elements into them. Puppet designers sometimes also work as autonomous artists, creating outside a performance context. \n', '2163.1.7'),
(50, 86, 'Occupation', 'http://data.europa.eu/esco/occupation/0f7c84c5-1c0d-4fc1-8949-07dbe8bd34ef', 'textile designer', 'print designer\ntextile design expert\nknits designer\nfabric designer\ntextiles designer\ntextile artist\ncanvas pattern designer\ntextile design specialist\npattern design\ntextile and graphic artist\ndesign and technology technician\ndesign technology technician\ntextile CAD designer', '', 'released', '2016-07-05 16:16:50', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Textile designers conceptualise textile products considering visual communication and functional performance.', '2163.1.8'),
(51, 88, 'Occupation', 'http://data.europa.eu/esco/occupation/00ab29a3-6d4a-4df9-b46d-de31069e36e8', 'mine surveyor', 'planner of mining operations\nmine plan maker\nmine operations surveyor\nmine surveyors\nmine records keeper\nmine planning surveyor\nmining surveyor', '', 'released', '2016-07-05 17:09:16', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Mine surveyors prepare and maintain mining plans in line with statutory and management requirements. They keep records of the physical progress of mining operations and of ore or mineral production.', '2165.4.2'),
(52, 89, 'Occupation', 'http://data.europa.eu/esco/occupation/00030d09-2b3a-4efd-87cc-c4ea39d27c34', 'technical director', 'technical and operations director\nhead of technical\ndirector of technical arts\nhead of technical department\ntechnical supervisor\ntechnical manager', '', 'released', '2016-07-05 13:58:41', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Technical directors realise the artistic visions of the creators within technical constraints. They coordinate the operations of various production units, such as scene, wardrobe, sound and lighting, and make-up. They adapt the prototype and study the feasibility, implementation, operation and technical monitoring of the artistic project. They are also responsible for the stage equipment and technical equipment.', '2166.4.7'),
(53, 89, 'Occupation', 'http://data.europa.eu/esco/occupation/11830794-108c-48fe-a282-2cd7a2945344', 'gambling, betting, and lottery games designer', 'lottery, betting, and gaming games designer\nbetting, gambling, and lottery games designer\nbetting, lottery, and gaming games designer\ngaming, betting, and lottery games designer\nlottery, gaming, and betting games designer\nbetting, lottery, and gambling games designer\ngambling games designer\ngaming, lottery, and betting games designer\ngambling, lottery, and betting games designer\nlottery games creator\ngambling games creator\nlottery, betting, and gambling games designer\nbetting games creator\ngames creator\nbetting, gaming, and lottery games designer\nlottery, gambling, and betting games designer\ngames designer\nlottery games designer', '', 'released', '2016-07-05 13:54:37', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Gambling, betting, and lottery games designers design innovative gambling, betting and lottery games. They determine the design, gaming rules or structure of a game.', '2166.9'),
(54, 102, 'Occupation', 'http://data.europa.eu/esco/occupation/08984bec-31d8-4bb0-aa2f-d557761ff029', 'specialised veterinarian', 'porcine health management veterinarian\nwildlife health management veterinarian\nveterinary pathologist\nsports medicine and rehabilitation veterinarian\nsmall mammal medicine veterinarian\nveterinary dermatologist\nemergency medicine veterinarian\nveterinary oncologist\nherpetological medicine veterinarian\nbovine health management veterinarian\nveterinary cardiologist\npoultry veterinary scientist\nanimal behavioural medicine veterinarian\nspecialized vet\nveterinary comparative nutritionist\nveterinary dentist\nveterinary theriogenologist\nveterinary neurologist\nveterinary ophthalmologist\nveterinary microbiologist\ncompanion animal internal medicine veterinarian\nveterinary clinical pathologist\nveterinary anaesthetist\nveterinary parasitologist\nzoological medicine veterinarian\nlaboratory animal veterinarian\nsmall ruminant health management veterinarian\nveterinary pharmacologist and toxicologist\nequine internal medicine veterinarian\naquatic animal health veterinarian\nspecialized vets\ndiagnostic imaging veterinarian', '', 'released', '2017-02-02 12:13:41', 'http://data.europa.eu/esco/regulated-professions/regulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Specialised veterinarians are professionals with a comprehensive scientific education. They have the authority to carry out, in an independent, ethical and personally responsible capacity, all aspects of veterinary medicine, in the interest of the health and welfare of animals and public health in accordance with national and international legislation. In addition they need a recognized qualification and/or experience in a specific species and/or veterinary procedure. \n \nVeterinary specialisms are generally classified according to species and/or procedures, for example:\n\n \n  Species covered may include equine and zoological animals \n  Procedures covered may include cardiology, orthopaedics \n \nCurrent lists ', '2250.5'),
(55, 105, 'Occupation', 'http://data.europa.eu/esco/occupation/0f845e64-de8b-431e-9fe3-1bccf8e1c5b9', 'pharmaceutical quality specialist', 'pharmaceutical quality technician\npharmaceutical quality control specialist\npharmaceutical quality control technician\nspecialist of pharmaceutical quality compliance\npharmaceutical quality inspector\nspecialist of pharmaceutical quality control\npharmaceutical quality manager\npharmaceutical product quality manager\npharmaceutical quality compliance specialist\npharmaceutical quality verifier\nspecialist of pharmaceutical quality', '', 'released', '2017-01-17 13:15:00', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Pharmaceutical quality specialists perform inspections and precision measurements in order to test and ensure the quality of pharmaceutical products. They are involved in the whole development phase of a pharmaceutical product until it is ready for the market. This includes the process of acquiring clinical trial licences, advising the pharmaceutical development staff on regulatory requirements and evaluating the content of the package leaflet and other documentation on the product. Moreover, pharmaceutical quality specialists collect and evaluate information on side effects of the product and communicate this knowledge both internally and to the relevant authorities.', '2262.1.1'),
(56, 106, 'Occupation', 'http://data.europa.eu/esco/occupation/00ab5610-e715-428f-99f6-b1e5e469dbcd', 'food safety specialist', 'food production quality controller\ntrainee food safety specialist\nfood safety controller\nsenior food safety specialist\nfood scientist\nfood safety monitor\nfood safety advisor\nfood safety technician\nfood compliance manager (food safety)\nassistant food safety specialist\nHACCP compliance manager\nfood safety consultant\ntechnical manager for food safety\nfood hygiene specialist\nfood quality controller', '', 'released', '2017-02-02 10:19:58', 'http://data.europa.eu/esco/regulated-professions/unregulated', 'Includes people performing activities at the point of sale.', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Food safety specialists organise processes and implement procedures to avoid problems with food safety. They comply with regulations.\n', '2263.2'),
(57, 107, 'Occupation', 'http://data.europa.eu/esco/occupation/006cc1f9-2841-41c3-991a-dc3f2f3bd533', 'physiotherapist', 'business physiotherapist\nhydrotherapist\noccupational physiotherapist\nosteopath\noccupational therapist\nbobath physiotherapist\nhealth and well-being therapist\nchiropractic therapist\nsports physiotherapist\nrespiratory therapist\neurythmy therapist\nhealth promotion worker\nremedial physiotherapist\nphysiotherapist manager\nmanipulative physiotherapist\nrespiratory physiotherapist\nneuromuscular physiotherapist\nneurological physiotherapist\nphysical therapist\nrehabilitation therapist\nmasseuse\nchiropractor\nneuromusculoskeletal physiotherapist\ncardiovascular physiotherapist\nneurodevelopmental physiotherapist', '', 'released', '2017-01-17 13:34:34', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Physiotherapists are autonomous health professionals who are responsible for developing, maintaining or restoring motor function and movement throughout the lifespan using evidence-based practice. They relieve pain and treat or prevent physical conditions associated with injury, disease or other impairments. Physiotherapists empower patients and their carers to manage the condition outside clinical settings. They work within their scope of practice and their professional Code of Conduct.', '2264.3'),
(58, 111, 'Occupation', 'http://data.europa.eu/esco/occupation/11a6d0f6-e692-4774-aa5f-193b67d31022', 'kinesiologist', 'kinesiology science researcher\nexercise scientist\npsychomotor kinesiologist\nexercise science researcher\nexercise kinesiologist\nexercise research scientist\nbiomechanical kinesiologist\nkinesiology scientist\nexercise researcher\nkinesiology researcher\nkinesiologists\nkinesiology research scientist', '', 'released', '2016-07-05 16:20:22', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Kinesiologists study and research the movement of the body; its muscles and parts. They analyse and use scientific data and methods to improve body motion, generally in humans, through an understanding of such areas as physiology, kinetics, neurology, and biology. They recognise the effects that some factors, such as body condition, have on motion and develop solutions to improve overall mechanics and mobility.', '2269.3'),
(59, 114, 'Occupation', 'http://data.europa.eu/esco/occupation/00747307-6341-4266-a061-25416f7c6a96', 'performing arts theatre instructor', 'acting school teacher\nconservatory theatre instructor\nprofessor of theatre\ntheatre instructor\nacting school instructor\nconservatory theatre lecturer\ninstructor in performing arts\ntheatre instructor in performing arts school', '', 'released', '2017-02-15 09:02:04', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Performing arts theatre instructors educate students in specific theory and, primarily, practice-based theatre courses at a specialised theatre, or acting, school or conservatory at a higher education level. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master in theatre. Performing arts theatre instructors monitor the students\' progress, assist individually when necessary, and evaluate their knowledge and performance of theatre practice through assignments, tests and examinations.', '2310.1.30'),
(60, 114, 'Occupation', 'http://data.europa.eu/esco/occupation/0959cd1d-f6c8-4362-939a-ad7d5f75d659', 'economics lecturer', 'economics teacher\nuniversity economics lecturer\nlecturer in economics\nuniversity lecturer in economics\nprofessor of economics\nsenior lecturer in economics\neconomics lector\neconomics professor\nhigher education economics teacher\nuniversity economics teacher\nlecturer of economics\neconomics instructor\neconomics docent', '', 'released', '2016-12-22 21:31:38', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Economics lecturers are subject professors, assistant professors, teachers, lectures, assistant lecturers, mentors who instruct students in their own specialised field of study, economics. They develop curriculum, prepare classes (lectures, practical classes, seminars, trainings etc.), monitor learning outcomes, supervise student study path. They conduct academic research in their field of economics and present their findings at the conferences and in publications. They are involved in some university management functions.', '2310.1.14'),
(61, 114, 'Occupation', 'http://data.europa.eu/esco/occupation/0bf5d25a-2b4d-43b5-b053-beeb210fa43a', 'medicine lecturer', 'clinical lecturer\nuniversity lecturer in medicine\ninstructor of medicine\nmedicine instructor\nlecturer of medicine\nprofessor of medicine\nmedicine lector\nuniversity teacher of medicine\nmedicine professor\nhigher education teacher of medicine\nclinical lecturer in medicine\nmedicine teacher\nlecturer in medical education\nlecturer in medicine\nmedicine docent\nuniversity medicine lecturer', '', 'released', '2016-12-22 21:31:54', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Medicine lecturers are subject professors, teachers, or lecturers, and often doctors who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, medicine, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants', '2310.1.25'),
(62, 114, 'Occupation', 'http://data.europa.eu/esco/occupation/1092dd4c-b96b-4b3f-87fe-4a9e8d1937fb', 'university teaching assistant', 'higher education teaching assistant\nteaching assistant\nuniversity teaching aide\nTA\nteaching assistant in higher education\nteaching assistant in universities\ngraduate teaching assistant', '', 'released', '2016-07-05 16:06:25', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'University teaching assistants are graduate students or recent graduates employed on a temporary contract at a university or a college for teaching-related responsibilities. They assist the professor, lecturer or teacher of the specific course they are in charge of in the preparation of lectures and of exams, grading papers and exams and leading review and feedback sessions for the students.', '2310.1.42'),
(63, 114, 'Occupation', 'http://data.europa.eu/esco/occupation/116b4c55-5bd0-41e9-9227-98d7ecffeaf6', 'sociology lecturer', 'instructor in sociology\nsenior lecturer in sociology\nsocial science lecturer\nhigher education sociology teacher\nlecturer of sociology\nspecialist lecturer in sociology\nlecturer in sociology\nsocial science instructor\nsocial science teacher\nuniversity sociology lecturer\nlecturer of social science\nsociology teacher\nsociology docent\nsocial science lector\nlecturer in social science\nprofessor of sociology\nsocial science professor\nsocial sciences lector\nsocial science docent\nsociology lector\nsociology instructor\nsociology professor', '', 'released', '2016-12-22 21:31:45', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Sociology lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, sociology, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants \nfor the preparation of lectures and of exams, grading papers and exams and leading review and feedback sessions for the students. They also conduct academic research in their field of sociology, publish their findings and liaise with other university colleagues.', '2310.1.38'),
(64, 116, 'Occupation', 'http://data.europa.eu/esco/occupation/0599d0e7-398f-464b-97bb-427686f27d50', 'maritime instructor', 'boating instructor\nship pilot trainer\nspecialist maritime training instructor\nmaritime trainer\nmaritime training instructor\noccupational boating instructor\ninstructor in maritime training\nship pilot instructor', '', 'released', '2016-07-05 16:04:38', 'http://data.europa.eu/esco/regulated-professions/unregulated', 'Includes people performing recreational boating instruction and sailing instruction not for proffesional means.', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Maritime instructors teach all those occupationally piloting a boat or a ship, such as skippers and ship captains, how to operate their inland water boats or maritime ships according to regulations. They teach their students theory and techniques on how to optimally pilot, steer and navigate, and maintain their specific boat or ship, observe and evaluate the students\' practice. They also focus on non-steering related subjects such as customer service (in case of person transportation) and safety measure regulations.', '2320.1.17'),
(65, 118, 'Occupation', 'http://data.europa.eu/esco/occupation/0d595b87-a1fc-410c-abbe-88b92cb0d334', 'ICT teacher secondary school', 'high school information and communication technology teacher\nteacher of ICT in high school\ncomputing teacher\nhigh school ICT and computing teacher\nICT teacher\nhigh school computing teacher\nsecondary school computing teacher\nhigh school ICT teacher\nsecondary school ICT tutor\nteacher of ICT in secondary schools', '', 'released', '2016-12-22 21:32:56', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'ICT teachers at secondary schools provide education to students, commonly children and young adults, in a secondary school setting. They are usually subject teachers, specialised and instructing in their own field of study, ICT. They prepare lesson plans and materials, monitor the students progress, assist individually when necessary, and evaluate the students knowledge and performance on the subject of ICT through assignments, tests and examinations.', '2330.1.9'),
(66, 120, 'Occupation', 'http://data.europa.eu/esco/occupation/03d79393-7b30-4159-945b-01963eddd302', 'primary school teacher', 'primary school music teacher\nprimary school educator\neducator in primary school\nprimary education teacher\nteacher in a primary school\nmusic teacher primary school\neducator in primary education\nteacher in primary education\nprimary education professional', '', 'released', '2017-01-17 13:17:03', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Primary school teachers instruct students on a primary school level. They develop lesson plans in line with curriculum objectives for the variety of subjects they teach, including mathematics, languages, nature studies and music. They monitor the students\' learning development and evaluate their knowledge and skills on the subjects taught through tests. They build their course content on the students\' knowledge of previous learnings and encourage them to deepen their understanding on the subjects they are interested in. They use class resources and teaching methods to create an inspiring learning environment. Primary school teachers also contribute to school events and communicate with parents and administrative staff.', '2341.1'),
(67, 124, 'Occupation', 'http://data.europa.eu/esco/occupation/02f1ae71-f89c-47b7-af2c-50d7ab1d9b8a', 'adult literacy teacher', 'instructor in adult literacy and numeracy\nteacher of adult literacy and numeracy\nadult literacy instructor\nadult literacy lecturer\nliteracy tutor\nteacher of adult literacy\nskills for life teacher\nadult literacy and numeracy instructor', '', 'released', '2016-12-22 21:32:23', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Adult literacy teachers instruct adult students, including recent immigrants and early school leavers, in basic reading and writing skills, usually on primary school level. Adult literacy teachers involve the students in the planning and executing of their reading activities, and assess and evaluate them individually through assignments and examinations.', '2352.1.1'),
(68, 124, 'Occupation', 'http://data.europa.eu/esco/occupation/034cad59-e666-4770-a7ef-a337257f8072', 'special educational needs teacher secondary school', 'teacher in special educational needs in secondary schools\nhigh school special education teacher\nsecondary school SEN teacher\nsecondary school special educational needs teacher\nhigh school special needs teacher\nspecial educational needs teacher in high school\nsecondary school special education teacher\nhigh school SEN teacher\nhigh school special educational needs teacher', '', 'released', '2016-12-22 21:31:16', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Special educational needs teachers at secondary schools provide specially-designed instruction to students on with a variety of disabilities on a secondary school level and ensure they reach their learning potential. Some special educational needs teachers at secondary schools work with children who have mild to moderate disabilities, implementing a modified curriculum to fit each student\'s specific needs. Other special educational needs teachers at secondary schools assist and instruct students with intellectual disabilities and autism, focusing on teaching them basic and advanced literacy, life and social skills. All teachers assess the students\' progress, taking into account their strengths and weaknesses, and communicate their findings to parents, counselors, administrators and other parties involved.', '2352.1.6'),
(69, 124, 'Occupation', 'http://data.europa.eu/esco/occupation/1044dedc-81d1-48b6-96b3-ced5393b2de1', 'teacher of talented and gifted students', 'teacher for students with high learning potential\nteacher for gifted students\ntalented and gifted pupils co-ordinator\ntalented and gifted students lead teacher\nteacher for talented students\ntalented and gifted pupils lead teacher\nteacher for pupils with high learning potential\nteacher for gifted pupils\ngifted pupils teacher\nteacher for talented pupils', '', 'released', '2017-01-17 11:33:18', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Talented and gifted students co-ordinators ensure suitable education is provided to talented and gifted students, commonly children and young adults, on various levels. They oversee the implementation of the school\'s gifted policy, monitor the students progress, and suggest extra activities to stretch and stimulate.', '2352.2'),
(70, 124, 'Occupation', 'http://data.europa.eu/esco/occupation/114c5a6c-1869-4a67-8ec0-660ec303f8cc', 'learning support teacher', 'learning support practitioner\nlearning support educator\nteacher in learning support\ncompensatory teacher\nearly intervention specialist\ndevelopmental teacher\ndevelopment support teacher\npractitioner of learning support teaching\nbasic skills teacher', '', 'released', '2016-12-22 21:32:04', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Learning support teachers assist students who have general learning difficulties. Learning support teachers focus on basic skills such as numeracy and literacy and thus teach basic subjects such as writing, reading, math and languages and they work for an educational institution such as a primary or secondary school. They support students in their school work, plan learning strategies, identify their learning needs and progress, and act accordingly. They can work in various educational set-ups and act as support for other teachers or manage their own class.', '2352.1.3'),
(71, 133, 'Occupation', 'http://data.europa.eu/esco/occupation/0ecedad1-1144-45ff-83db-a3c4db393036', 'investment manager', 'investment consultant\nglobal investment manager\ninvestment executive\ninvestments portfolio manager\nportfolio analyst\ninvestments manager\ninvestment portfolio manager', '', 'released', '2017-01-17 13:09:23', 'http://data.europa.eu/esco/regulated-professions/unregulated', 'Includes people performing financial planning, strategy and research.', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Investment managers administer the portfolio of investments that a company has. They perform close follow up of the investments looking for the most profitable solutions represented in financial products or securities. They analyse behaviour in financial markets, interests rates, and the companies\' position in order to advise on risks and profitability for the client.', '2412.5.3'),
(72, 134, 'Occupation', 'http://data.europa.eu/esco/occupation/076d23f3-a689-4f1a-8b55-ae870c9aaa85', 'securities analyst', 'securities research director\nshares analyst\ncorporate securities research analyst\nsecurities research analyst\nrating analyst\nbonds analyst\nstock analyst', '', 'released', '2017-01-17 13:30:53', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Securities analysts perform research activities to gather and analyse financial, legal and economic information. They interpret data on the price, stability and future investment trends in a certain economic area and make recommendations and forecasts to business clients.', '2413.1.5'),
(73, 137, 'Occupation', 'http://data.europa.eu/esco/occupation/01205776-db8d-4588-a6a6-56d0b9c660a9', 'housing policy officer', 'policy officer, planning and housing\nhousing policy development officer\nhousing policy researcher\npolicy officer\nhousing policy advisor\nhousing policy analyst\naffordable housing policy officer\nsenior housing policy officer\nhousing policy and strategy officer', '', 'released', '2017-01-17 14:48:59', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Housing policy officers research, analyse and develop housing policies which enable affordable and adequate housing for all. They implement these policies to improve the housing situation of the population by measures such as building affordable housing, supporting people to buy real estate and improving conditions in existing housing. Housing policy officers work closely with partners, external organisations or other stakeholders and provide them with regular updates.', '2422.10.8'),
(74, 138, 'Occupation', 'http://data.europa.eu/esco/occupation/068370e0-9c27-4814-87d0-44fbb3140815', 'recruitment consultant', 'job placement officer\nemployee recruiter\ntemporary labour officer\nrecruiting process consultant\nrecruitment officer\nhead hunter\ntemporary labour agent\nemployment agent\njob placement consultant', '', 'released', '2017-01-17 14:06:31', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Recruitment consultants provide the suitable candidates to employers according to the specific job profile requested. They perform testing and interviewing with job seekers, shortlist few candidates to present to the employers and match candidates to appropriate jobs. Recruitment consultants maintain relationships with employers to offer their services on a more long-term basis.', '2423.7'),
(75, 138, 'Occupation', 'http://data.europa.eu/esco/occupation/070e94a8-a933-4557-9b95-fefe7f4f1763', 'public employment service manager', 'jobcentre manager\njobcentre plus manager\nemployment service manager\nDWP manager\nnational careers service manager\ncareers advice manager\ndepartment of work and pensions manager\njob centre manager', '', 'released', '2017-02-24 09:53:23', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Public employment service managers supervise the operations of a public employment agency. They oversee the staff that helps people find employment or provide occupational guidance.', '2423.6'),
(76, 139, 'Occupation', 'http://data.europa.eu/esco/occupation/0ba06640-e0ac-4911-9e43-289a8e41651e', 'corporate trainer', 'in-company trainer\nskills development coach\nworkplace training specialist\nprofessional development coach\ncorporate education professional\ntrainer in corporate sector\ntrainer of corporate employees\ncorporate training specialist\nskill development trainer\nskill development coach\ncorporate professional trainer\neducational planner', '', 'released', '2017-02-15 15:04:47', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Corporate trainers train, coach, and guide employees of a company to teach and improve their skills, competences and knowledge in accordance with the needs of the company. They develop the existing potential of the employees to increase their efficiency, motivation, job satisfaction, and employability.', '2424.2'),
(77, 141, 'Occupation', 'http://data.europa.eu/esco/occupation/002da35b-7808-43f3-83bf-63596b8b351f', 'hospitality revenue manager', 'hospitality revenues manager\nyield manager\nhospitality yields manager\nrevenue manager', '', 'released', '2017-01-17 13:33:42', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Hospitality revenue managers maximise revenue generated from facilities such as hotels, ', '2431.9'),
(78, 142, 'Occupation', 'http://data.europa.eu/esco/occupation/13427968-250b-4967-a682-afac5cd0af4f', 'campaign canvasser', 'campaign canvass worker\npolitical campaign canvasser\nparty campaigner\npolitical canvasser\ncanvasser\npolitical party activist\nparty activist\npolitical party volunteer\ncampaign worker', '', 'released', '2016-07-05 16:26:46', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Campaign canvassers operate on field level to persuade the public to vote for the political candidate they represent. They engage in direct conversation with the public in public places, and gather information on the public\'s opinion, as well as perform activities ensuring that information on the campaign reaches a wide audience.', '2432.6.1'),
(79, 142, 'Occupation', 'http://data.europa.eu/esco/occupation/137b1d11-c4bc-4016-89ba-45345d4c71f0', 'online community manager', 'interactive media specialist\nonline community specialist\nsocial media manager\nsocial media strategist\nsocial media reachout manager\nonline reachout manager\nonline content and community manager\ndigital community manager\nsocial media specialist\nonline community managers', '', 'released', '2016-07-05 17:00:17', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Online community managers provide and maintain an interactive environment facilitated by applications such as social media, forums and wikis. They maintain the relations between different digital communities.', '2432.5'),
(80, 143, 'Occupation', 'http://data.europa.eu/esco/occupation/114e1eff-215e-47df-8e10-45a5b72f8197', 'renewable energy consultant', 'renewable energy technical consultant\nsustainable energy consultant\nrenewable energy research consultant\nrenewable energy market consultant\nsustainable energy research consultant\nsustainable energy market consultant\nsustainable energy technical consultant\nrenewable energy survey consultant\nsustainable energy survey consultant', '', 'released', '2016-07-05 16:08:43', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Renewable energy consultants advise clients on the advantages and disadvantages of different renewable energy sources. They conduct surveys and interviews to research demand of and opinions on renewable energy, and strive to advise clients on the most advantageous source of renewable energy for their purpose.', '2433.3'),
(81, 147, 'Occupation', 'http://data.europa.eu/esco/occupation/07e60525-1aad-4099-aaf3-2c7014c92212', 'integration engineer', 'software integration engineer\nICT integration engineers\nIT integration engineer\nsystem integration engineer', '', 'released', '2017-02-02 12:40:32', 'http://data.europa.eu/esco/regulated-professions/unregulated', 'Includes people performing managerial activities.', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Integration engineers develop and implement solutions which coordinate applications across the enterprise or its units and departments. They evaluate existing components or systems to determine integration requirements and ensure that the final solutions meet organisational needs. They reuse components when possible and assist management in taking decisions. They perform ICT system integration troubleshooting.', '2511.16'),
(82, 147, 'Occupation', 'http://data.europa.eu/esco/occupation/10469d70-78a3-4650-9e29-d04de13c62c1', 'embedded system designer', 'embedded system developer\nembedded software developer\nembedded system programmer\nembedded systems designers\nembedded systems developer\nembedded systems programmer', '', 'released', '2017-02-02 13:19:12', 'http://data.europa.eu/esco/regulated-professions/unregulated', 'Excludes people performing programming and coding activities.', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Embedded system designers translate and design requirements and the high-level plan or architecture of an embedded control system according to technical software specifications.', '2511.4'),
(83, 148, 'Occupation', 'http://data.europa.eu/esco/occupation/04ba4d6c-957d-417f-bf63-5b9e015a9f86', 'software analyst', 'programming analyst\nsoftware requirement analyst\nsoftware analysts\nsoftware requirements analyst\nanalyst of software\napplication analyst\nprogrammer analyst', '', 'released', '2016-12-21 12:09:45', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Software analysts elicit and prioritise user requirements, produce and document software specifications, test their application, and review them during software development. They act as the interface between the software users and the software development team.', '2512.1'),
(84, 149, 'Occupation', 'http://data.europa.eu/esco/occupation/092fdeeb-a76f-4ed2-b248-90fd6eaaffef', 'search engine optimisation expert', 'search engine optimisation specialist\nsearch engine optimiser\nSEO expert\nSEO specialist\ngrowth hacker\ninbound marketer\nSEO experts\nsearch engine optimisation experts\nsearch engine optimisation consultant\nsearch engine marketing specialist', '', 'released', '2016-07-05 17:01:40', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Search engine optimisation experts increase the ranking of a company\'s web pages with regard to target queries in a search engine. They create and launch SEO campaigns and identify areas of improvement. Search engine optimisation experts may conduct pay per click (PPC) campaigns.', '2513.2'),
(85, 151, 'Occupation', 'http://data.europa.eu/esco/occupation/0ab5c12c-c1c1-4772-8345-aa287d0e391d', 'ICT test analyst', 'test designer\nIT test analyst', '', 'released', '2016-07-05 17:08:30', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'ICT test analysts work in testing environments, assessing products, checking for quality and accuracy, or creating tests scripts. They design tests which are then implemented by testers.', '2519.5'),
(86, 151, 'Occupation', 'http://data.europa.eu/esco/occupation/102e75c8-3b47-4964-9b47-30a980aed25c', 'ICT disaster recovery analyst', 'IT resilience analyst\ndisaster recovery officer\nICT resilience analysts\nbusiness continuity strategist\nIT disaster recovery analyst\nbusiness continuity analyst', '', 'released', '2017-02-02 10:33:45', 'http://data.europa.eu/esco/regulated-professions/unregulated', 'Excludes people performing development activities.', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'ICT disaster recovery analysts develop, maintain, and implement ICT continuity and disaster recovery strategies and solutions. They support the technical teams, assess the risks, design and develop procedures, documentation and strategies for disaster recovery in order for business functions to continue and recover with a minimal loss of data. They also coordinate the system backups tests and validations.', '2519.3'),
(87, 151, 'Occupation', 'http://data.europa.eu/esco/occupation/106f79e4-6264-45f1-9e7a-297435cd684b', 'software tester', 'tester\napplication software tester\nsoftware testers\napplication tester\nsoft tester\nsoftware testing specialist\nmodule tester\nsoftware application tester\nunit tester', '', 'released', '2017-01-17 13:42:16', 'http://data.europa.eu/esco/regulated-professions/unregulated', 'Excludes people performing managerial activities.', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Software testers perform software tests. They may also plan and design them. They may also debug and repair software although this mainly corresponds to designers and developers. They ensure that applications function properly before delivering them to internal and external clients.', '2519.6'),
(88, 153, 'Occupation', 'http://data.europa.eu/esco/occupation/1562c7a3-c7d9-419d-b9b6-db26610bcf84', 'data warehouse designer', 'data warehouse architect\ndata warehouse developer', '', 'released', '2016-12-19 17:06:38', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Data warehouse designers are responsible for planning, connecting, designing, scheduling, and deploying data warehouse systems. They develop, monitor and maintain ETL processes, reporting applications and data warehouse design.', '2521.5'),
(89, 156, 'Occupation', 'http://data.europa.eu/esco/occupation/0464b062-cea6-4164-b10d-956c61956ae7', 'ICT security administrator', 'system security administrator\nnetwork security administrator\nICT security administrators\nIT security administrator', '', 'released', '2016-07-05 17:02:06', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'ICT security administrators plan and carry out security measures to protect information and data from unauthorised access, deliberate attack, theft and corruption.', '2529.5'),
(90, 156, 'Occupation', 'http://data.europa.eu/esco/occupation/0c448a27-10ec-43ba-b880-d9938bade424', 'digital forensics expert', 'ICT forensics expert\ninformation forensics expert\ncomputer forensics expert\ncomputer forensics investigator\ndigital forensics analyst\ncyber forensics expert\ndigital forensics specialist\ndigital forensic expert\ndigital forensics experts', '', 'released', '2016-12-20 09:30:39', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Digital forensics experts retrieve and analyse information from computers and other types of data storage devices. They examine digital media that may have been hidden, encrypted or damaged, in a forensic manner with the aim to identify, preserve, recover, analyse and present facts and opinions about the digital information.', '2529.2'),
(91, 156, 'Occupation', 'http://data.europa.eu/esco/occupation/0ce5a9f4-e00a-4bbe-b255-3c63407167a4', 'ICT security consultant', 'IT security expert\nIT security consultant\nICT security consultants\ninformation communications technology security consultant\nconsultant in ICT security activities\nIT security advisor\nICT security advisor\ninformation technology security consultant', '', 'released', '2016-07-05 16:59:21', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'ICT security consultants advise and implement solutions to control access to data and programs. They promote a safe exchange of information.\n', '2529.6'),
(92, 160, 'Occupation', 'http://data.europa.eu/esco/occupation/07bd65d7-c13f-443a-bdf6-88903f3822be', 'judge', 'investigating magistrate\ndeputy circuit judge\ndistrict judge\ncircuit judge\nhigh court judge\nfamily court judge\ninquisitorial magistrate\nyouth court judge\nexamining magistrate\nappeal court judge\nrecorder\nadjudicator', '', 'released', '2017-01-17 12:08:53', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Judges preside over, review and handle court cases, hearings, appeals and trials. They ensure that court procedures conform to conventional legal processes and review evidence and juries. Judges preside over cases involving such areas as crime, family issues, civil law, small claims and juvenile offenses.', '2612.1'),
(93, 166, 'Occupation', 'http://data.europa.eu/esco/occupation/12ff7f9c-c5b5-4d4a-b585-c350dd1400eb', 'business economics researcher', 'business economist\nfinancial market researcher\nbusiness researcher\nfiscal economics strategist\nfiscal economics researcher\nfiscal economics analyst\nbusiness research scientist\neconomic analyst\nbusiness economy researcher\nfinancial market economist\neconomic advisor\nfinancial market advisor\nbusiness advisor\nbusiness analyst\nfiscal economics advisor', '', 'released', '2017-01-17 12:32:39', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Business economics researchers conduct research on topics regarding economy, organisations, and strategy. They analyse macroeconomic and microeconomic trends and use this information to analyse the positions of industries or specific companies in the economy. They provide advice regarding strategic planning, product feasibility, forecast trends, emerging markets, taxing policies, and consumer trends.', '2631.1.1'),
(94, 167, 'Occupation', 'http://data.europa.eu/esco/occupation/0611f232-b30e-46c7-9c26-2d59b1448e79', 'criminologist', 'criminology science researcher\ncriminology studies scholar\ncriminology studies scientist\ncriminology research scientist\ncriminology scientist\ncriminology researcher\ncriminology scholar\ncriminology studies researcher\ncriminologists\ncriminology studies research scientist', '', 'released', '2016-07-05 16:21:52', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Criminologists study conditions pertaining to humans such as the social and psychological aspects that could lead them to commit criminal acts. They observe and analyse different factors ranging from behavioural conditions up to social background and environment of suspects in order to advise organisations on the prevention of crime.', '2632.4');
INSERT INTO `occupation` (`id`, `isco_group_id`, `concept_type`, `concept_uri`, `preferred_label`, `alt_labels`, `hidden_labels`, `status`, `modified_at`, `regulated_profession_note`, `scope_note`, `definition`, `in_scheme`, `description`, `code`) VALUES
(95, 167, 'Occupation', 'http://data.europa.eu/esco/occupation/11df8941-508c-4103-ad40-52cdf9430a59', 'sociologist', 'sociology research analyst\nrural sociology analyst\nrural sociology scholar\nrural sociology research scientist\nrural sociologist\neconomic sociology analyst\nurban sociology scientist\ngerontologist\nrural sociology science researcher\neconomic sociology science researcher\neconomic sociology scholar\nurban sociology analyst\nsociology scientist\neconomic sociology scientist\nintercultural communications adviser\nfamily and consumer science researcher\nrural sociology scientist\nurban sociology research analyst\nsociology science researcher\nsociology researcher\nurban sociology research scientist\nsociologists\nurban sociologist\nurban sociology science researcher\nsociology research scientist\neconomic sociologist\nsociology scholar\nurban sociology researcher\nurban sociology scholar\neconomic sociology research scientist\nsociology analyst\nrural sociology researcher\nlaw sociologist\neconomic sociology research analyst\nrural sociology research analyst\neconomic sociology researcher', '', 'released', '2017-01-17 14:21:06', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Sociologists focus their research on explaining social behaviour and the way people have organised themselves as a society. They research and explain the way societies have evolved by describing their legal, political, and economic systems and their cultural expressions.', '2632.6'),
(96, 168, 'Occupation', 'http://data.europa.eu/esco/occupation/0cd729a6-ed11-4338-ac4b-d4b924390128', 'historian', 'diplomatic historian\nhistorians\neconomic historian\negyptologist\nmilitary historian\npolitical historian\nart historian', '', 'released', '2016-07-05 16:18:49', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Historians research, analyze, interpret, and present the past of human societies. They analyse documents, sources, and traces from the past in order to understand the past societies.', '2633.1'),
(97, 170, 'Occupation', 'http://data.europa.eu/esco/occupation/0d423a2e-6fb7-4704-8544-bd6c522b3c62', 'child care social worker', 'social worker (child protection)\nsocial worker (looked after children)\nchildren\'s social worker\nsocial worker (children and families)\nchildren\'s services practitioner\nsocial worker family safeguarding team\nchildcare social worker\nchild protection social worker\nsocial worker (child care)', '', 'released', '2016-07-05 16:26:17', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Child care social workers provide social services to children and their families in order to improve their social and psychological functioning. They aim to maximize family\'s well-being and protect children from abuse and neglect. They assist adoption arrangements and find foster homes where needed.', '2635.3.2'),
(98, 171, 'Occupation', 'http://data.europa.eu/esco/occupation/0f086907-96a6-4e88-bf00-a84295125a6a', 'missionary', 'missions pastor\ncity missionary\nhumanitarian aid worker\nchristian social worker\nmissionary preacher\ncatholic humanitarian worker\nintercessory missionary', '', 'released', '2016-07-05 16:26:10', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Missionaries supervise the execution of missions of outreach from a church foundation. They organise the mission and develop the mission\'s goals and strategies, and ensure the mission\'s goals are executed, and policies implemented. They perform administrative duties for record maintenance, and facilitate communication with the relevant institutions in the mission\'s location.', '2636.3'),
(99, 177, 'Occupation', 'http://data.europa.eu/esco/occupation/09a40a9a-2b60-422c-abe4-96d829bc27df', 'art restorer', 'artifact conservator\nrestoration technician\nrestorers\nrestoration artist\nrestorer\nart objects repairer\npreserver', '', 'released', '2020-06-10 16:10:01', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Art restorers work to', '2651.2'),
(100, 178, 'Occupation', 'http://data.europa.eu/esco/occupation/0e216d47-fc12-43e9-8953-ad8ba11f4b96', 'musical conductor', 'choir leader\nband leader\nchoir director\nmusic conductor\norchestra music director\nprincipal conductor\nconcert master\norchestra conductor\nchoir conductor\nmusical director\nchoral conductor\nband master', '', 'released', '2016-10-17 16:00:53', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Musical conductors lead ensembles of musicians directing them during rehersals and helping them atteining their best performance. They can work with a variety of ensembles such as choirs and orchestras. Musical conductors adjust the tempo (speed), rhythm, dynamics (loud or soft) and articulation (smooth or detached) of the music using gestures and sometimes dancing to motivate the musicians to play according to the music sheet.', '2652.3.1'),
(101, 178, 'Occupation', 'http://data.europa.eu/esco/occupation/13d8b1be-d711-4bbc-a45e-cad605162f41', 'music director', 'orchestra director\nband master\nconductor\nmusical director\nband director\norchestra conductor\nmusic leader\nmusic conductor', '', 'released', '2017-02-23 12:38:19', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Music directors lead musical groups such as orchestras and bands during live performances or recording sessions. They organise the music and composition, coordinate the playing musicians and record the performance. Music directors are professionals working in diverse places such as the film industry, music videos, radio stations, musical ensembles or schools.', '2652.2'),
(102, 179, 'Occupation', 'http://data.europa.eu/esco/occupation/06ac0f53-431e-4bc6-acb8-2f5da041b5d3', 'dance r', 'assistant choreographer\nassistant choreographer and repetiteur\ndance tutor\ndance rehearsal director\nassistant choreographer and r', '', 'released', '2017-02-02 13:06:52', 'http://data.europa.eu/esco/regulated-professions/unregulated', 'Excludes people working in an educational work context.', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Dance r', '2653.1.2'),
(103, 179, 'Occupation', 'http://data.europa.eu/esco/occupation/07b91f36-8272-493a-8e1c-5625e77efe68', 'dancer', 'dance artist\nshow dancer\ncontemporary dancer\nstage dancer\nballet dancer\nballet and modern dance perfomer\nsinger and dancer\ndance performer\nsoloist dancer\nballet and modern dancer\nmodern dancer\nbelly dancer\nmodern & ballet dance performer\nmodern and ballet dance performer\ndancer & singer\nballet & modern dance performer\nmodern and ballet dancer\njazz dancer\ngroup dancer\ndancer and singer\nmodern & ballet dancer\nsinger & dancer\nsinger-dancer\nballet & modern dancer\nsolo dancer\ncommercial dancer', '', 'released', '2017-01-17 13:02:54', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Dancers interpret ideas, feelings, stories or characters for audiences by using movement and body language mostly accompanied by music.  This normally involves interpreting the work of a choreographer or a traditional repertory, although it may sometimes require improvisation.', '2653.2'),
(104, 182, 'Occupation', 'http://data.europa.eu/esco/occupation/09a9a956-db70-436f-8398-d85a17f6cdfe', 'presenter', 'video jockey presenter\nprogram host\nsports presenter\nbroadcast journalist\nbroadcasting presenter\nsenior broadcast journalist\nfinance broadcast journalist\nsport announcer\ntelevision presenter\nVJ radio presenter\nTV presenter\nmoderator\nDJ radio presenter\nhost\nhostess\nfreelance broadcast presenter\nradio presenter\nannouncer\nbroadcaster\nbroadcast presenter', '', 'released', '2017-01-17 13:57:51', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Presenters host broadcast productions. They are the face or voice of these programs and make announcements on different platforms such as radio, television, theatres or other establishments. They ensure that their audience is entertained and introduce the artists or persons being interviewed.', '2656.3'),
(105, 182, 'Occupation', 'http://data.europa.eu/esco/occupation/0dce2f9b-eda5-4dc4-ba74-36eeed8c9a44', 'audio describer', 'audio description expert\nvideo describer\naudio description professional\nvisual describer\ndubber\naudio describers', '', 'released', '2017-01-17 14:27:43', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Audio describers depict orally what happens on the screen or on stage for the blind and visually impaired so that they can enjoy audio-visual shows, live performances or sports events. They produce audio description scripts for programmes and events and use their voice to record them.', '2656.1'),
(106, 183, 'Occupation', 'http://data.europa.eu/esco/occupation/080bce18-066b-4431-90ed-e07608f73fe0', 'street artist', 'muralist\nstreet caricaturist\nstreet visual artist\nlight street artist\ngraffiti artist\npaste-up street artist\nurban artist\nstencil artist\npictorial muralist', '', 'released', '2016-12-23 10:13:57', 'http://data.europa.eu/esco/regulated-professions/unregulated', 'Excludes outdoors performing artists.', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Street artists create visual art such as graffiti art or sticker art in urban environments\' public spaces, on the streets, typically expressing feelings or political views and ideas, opting for non-traditional art venues.', '2659.3'),
(107, 187, 'Occupation', 'http://data.europa.eu/esco/occupation/0d7ae791-653e-4297-9f61-8bad87f61d57', 'meteorology technician', 'atmospheric scientist technician\nmeteorological technician\nmetrologist assistant\nweather station operator\nmeteorology technician\nwind technician\naviation meteorology technician\nweather forecaster\nelectronics technician\nclimatological technician\nmeteorologist\nmeteorological researcher\nMET tower installation technician\nweather service specialist\natmospheric scientist\nmeteorological instrumentation technician\nMET services technician', '', 'released', '2017-01-17 12:37:02', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Meteorology technicians collect large amounts of meteorological information for weather information users such as aviation companies or meteorological institutions. They operate specialised measuring instruments to make accurate weather predictions and report their observations. Meteorology technicians assist meteorologists in their scientific operations.', '3111.8'),
(108, 188, 'Occupation', 'http://data.europa.eu/esco/occupation/09e070ce-0afe-40bb-be90-530e5d42cdbd', 'domestic energy assessor', 'domestic energy sales promoter\nresidential energy saleswoman\nresidential energy sales negotiator\nresidential energy sales promoter\ndomestic energy seller\nresidential energy salesman\ndomestic energy sales negotiator\nresidential energy seller\nresidential energy salesperson\nresidential energy sales rep\nresidential energy sales representative', '', 'released', '2017-01-17 13:21:43', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Domestic energy assessors advise individuals on energy supply for their homes. They assess the individual\'s needs and recommend the appropriate source of energy and supplier, attempting to secure energy sales. They also advise on economic and environmental advantages of types of energy, and create energy plans compliant with regulations and the technical requirements and conditions of the residence.', '3112.1.5.1'),
(109, 188, 'Occupation', 'http://data.europa.eu/esco/occupation/0f5cc4eb-925d-4b99-be6d-b0964a253a9a', 'civil engineering technician', 'civil engineering superintendent\nroad engineering superintendent\ncivil engineering services technician\ncivil engineering technical assistant\nearthworks civil engineering superintendent\ntechnician, civil engineering\ncivil engineering design technician\ncivils technician\nhydraulic engineering superintendent\ncivil engineering technical officer', '', 'released', '2017-01-17 13:16:58', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Civil engineering technicians help design and execute construction plans and take on organisational tasks, for example in the planning and monitoring, and in bidding and invoicing of construction work. They also calculate material requirements, and help with the purchasing and organising, and ensure the quality of the construction materials. Civil engineering technicians may perform technical tasks in civil engineering and', '3112.1'),
(110, 188, 'Occupation', 'http://data.europa.eu/esco/occupation/13331bd9-0599-47c0-9812-29650c218de3', 'water engineering technician', 'water pipeline systems engineering technician\nwater systems installation engineering technician\nflood defence engineering technician\nwater treatment systems engineering technician\nwater engineering technician\nwater supply systems engineering technician\nwater pumping systems engineering technician\nwater treatment engineering technician\nwater supply engineering technician\nwater resource engineering technician\ndrinking water systems engineering technician\nwater pipelines engineering technician\nflood prevention engineering technician\nclean water systems engineering technician', '', 'released', '2017-01-17 13:23:34', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Water systems engineering technicians aid engineers in the development and implementation of water supply and water treatment systems. They monitor the operations to ensure compliance with health and safety regulations, check water quality and ensure implementation of water-related legislation.', '3112.1.13'),
(111, 190, 'Occupation', 'http://data.europa.eu/esco/occupation/0ea36a48-a27d-4515-b61f-3cab395cf60f', 'microelectronics engineering technician', 'microelectronics technician\nmicro-electronics engineering technician\ntechnician in microelectronic engineering\ntechnologist in microelectronics\nmicroelectronics process engineering technician\nmicroelectronics technologist\ntechnologist in microelectronic systems\nmicroelectronic equipment technologist\nmicroelectronic systems technologist', '', 'released', '2017-01-26 11:52:45', 'http://data.europa.eu/esco/regulated-professions/unregulated', 'Excludes <a resource=\"http://data.europa.eu/esco/occupation/26e1add0-da3f-4ed6-aa91-791cb550146f\" typeOf=\"http://data.europa.eu/esco/model#Occupation\" href=\"http://data.europa.eu/esco/occupation/26e1add0-da3f-4ed6-aa91-791cb550146f\"><span property=\"http:/', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Microelectronics engineering technicians collaborate with microelectronics engineers in the development of small electronic devices and components such as micro-processors, memory chips, and integrated circuits for machine and motor controls. Microelectronics engineering technicians are responsible for building, testing, and maintaining the microelectronic systems and devices.', '3114.1.6'),
(112, 191, 'Occupation', 'http://data.europa.eu/esco/occupation/04849c5c-9236-4a75-aabb-11c6d03e459c', 'marine engineering technician', 'yacht  engineering technician\nmarine technologist\nvessel engineering technologist\nmarine engineering technologist\nsubmarine engineering technician\nvessel engineering technician\nvessel technologist\nboat engineering technician\nmarine vessel engineering technician\ncargo vessel engineering technician\nship engineering technician\nsubmarine engineering technologist\nsubmarine technologist\npleasure craft  engineering technician', '', 'released', '2016-07-05 16:54:09', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Marine engineering technicians carry out technical functions to help marine engineers with the design, development, manufacturing and testing processes, installation and maintenance of all types of boats from pleasure crafts to naval vessels, including submarines. They also conduct experiments, collect and analyse data and report their findings.', '3115.1.7'),
(113, 191, 'Occupation', 'http://data.europa.eu/esco/occupation/05a55bb3-68d6-4ff0-83fd-46a68a45a598', 'aerospace engineering technician', 'aeronautics engineering technician\naero spatial technologist\naeroplane engineering technician\nspaceship engineering technician\naeroplane technologist\naeronautical technologist\naircraft engineering technician\naircraft technologist\nspaceship technologist\nspacecraft engineering technician\nspacecraft technologist', '', 'released', '2016-07-05 17:06:16', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Aerospace engineering technicians work with aerospace engineers to operate, maintain and test equipment used on aircraft and spacecraft. They review blueprints and instructions to determine test specifications and procedures. They use software to make sure that parts of a spacecraft or aircraft are functioning properly. They record test procedures and results, and make recommendations for changes.', '3115.1.1'),
(114, 191, 'Occupation', 'http://data.europa.eu/esco/occupation/0f2c04f1-9001-4a77-884f-1ea16949f6f6', 'rolling stock engineering technician', 'rolling stock equipment assembler\nrolling stock electronic equipment installer\nrolling stock assembly operator\nrailway stock equipment assembler\nrail rolling stock assembly operator\nrolling stock engineering apprentice\nrailway stock electronic equipment installer\nrailway stock engineer\nrailway engineering apprentice', '', 'released', '2016-07-05 16:55:28', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Rolling stock engineering technicians carry out technical functions to help rolling stock engineers with the design, development, manufacturing and testing processes, installation and maintenance of rail vehicles such as wagons, multiple units, carriages and locomotives. They also conduct experiments, collect and analyse data and report their findings.', '3115.1.16'),
(115, 191, 'Occupation', 'http://data.europa.eu/esco/occupation/11833a96-fc27-4b03-a53d-892ac891a2bb', 'production engineering technician', 'production engineering instrument mechanic\nproduction engineering test inspector\nproduction engineering inspection mechanic\nproduction  maintenance inspector\nproduction engineering co-ordinator\nproduction engineering coordinator\nproduction engineering testing coordinator\nproduction engineering technician\nproduction engineering inspection supervisor\nproduction engineering mechanic\nproduction engineering inspection inspector\ndata collection technician\nproduction engineering supervisor\nproduction engineering maintenance co-ordinator', '', 'released', '2017-01-17 13:43:56', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Production engineering technicians plan production, follow up production processes and develop and test solutions to solve technical problems. They work closely with engineers and technologists, inspect products, conduct tests, conduct tests collect data.', '3115.1.15'),
(116, 192, 'Occupation', 'http://data.europa.eu/esco/occupation/00634fc4-802a-461b-8af0-499273756f99', 'asphalt laboratory technician', 'road surface technician\nmaterials technician\naggregates and bitumen laboratory technician\ntechnical supervisor, asphalt production\nsite technician\nbitumen production technician\nroad surface laboratory technician\nlaboratory technician, asphalt\nasphalt technician\nbitumen technician\nasphalt production technical supervisor\nsite technician, asphalt\naggregates and asphalt laboratory technician\nasphalt production technician', '', 'released', '2016-07-05 17:08:43', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Asphalt laboratory technicians perform asphalt and related raw materials inspections and laboratory testing, ensuring a high quality of the products. They also participate in coming up with resolutions to technical issues on construction sites.', '3116.1.1'),
(117, 193, 'Occupation', 'http://data.europa.eu/esco/occupation/06351e17-67c4-44bc-adf7-64b73f92c3b5', 'metallurgical technician', 'metallurgy test technician\nmining technician\nminerals technician\nquality technician, metals\nmetallurgy technician\ndevelopment metallurgist\nmetallurgical research technician\nmetallurgist\nmetallurgy research technician\nmetallurgical test technician\nextraction technician\nlaboratory metallurgist\nmetallurgy quality technician\nmetallurgical quality technician', '', 'released', '2017-01-17 12:08:45', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Metallurgical technicians provide technical support in researching and performing experiments on minerals, metals, alloys, oil and gas. They also assist in improving the methods of extraction.', '3117.3'),
