(121, 198, 'Occupation', 'http://data.europa.eu/esco/occupation/040f62f5-d570-4d3f-b732-c8ea4afc2738', 'container equipment assembly supervisor', 'container equipment sub assembler supervisor\ncontainer equipment assembly line senior operative\ncontainer equipment assembly line supervisor\ncontainer equipment assembly line machine supervisor\ncontainer equipment machine assembler supervisor\ncontainer equipment machine assembly supervisor\ncontainer equipment bench precision supervisor\ncontainer equipment electronic supervisor\ncontainer equipment assembly line senior operator\ncontainer equipment supervisor worker\ncontainer equipment production line supervisor\ncontainer equipment mechanical assembler supervisor\ncontainer equipment final supervisor\ncontainer equipment manufacturing assembler supervisor', '', 'released', '2016-12-19 12:10:37', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Container equipment assembly supervisors monitor the assembly process of containers such as boilers or pressure vessels. They train and coach the workers involved in the assembly to achieve production goals.', '3122.3.2'),
(122, 198, 'Occupation', 'http://data.europa.eu/esco/occupation/0e68f523-3206-4896-b725-44150dd22057', 'leather goods production supervisor', 'leather goods cutting room supervisor\nleather goods closing room supervisor\nleather product manufacturing forewoman\nleather product manufacturing foreman\nleather goods finishing room supervisor\nleather goods production inspector\nleather products supervisor\nleather tannery supervisor\nleather goods production forewoman\nleather goods production foreman', '', 'released', '2017-01-17 11:54:07', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Leather goods production supervisors monitor and coordinate day-to-day production activities of a leather goods manufacturing plant. They oversee quality control as well as manage the leather goods production staff. The work also involves organising the work flow as well as taking care of production plan and costs.', '3122.4.8'),
(123, 198, 'Occupation', 'http://data.europa.eu/esco/occupation/139fba7b-6c1f-40ee-8acf-78f97e57c905', 'waste management supervisor', '', '', 'released', '2016-07-05 16:02:21', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Waste management supervisors coordinate waste collection, recycling, and disposal facilities. They supervise waste management operations, ensuring compliance with environmental standards, and oversee staff. They aid in the development of waste management methods, aiming for increased waste reduction, and aid in the prevention of violations of waste treatment legislation.', '3122.5'),
(124, 199, 'Occupation', 'http://data.europa.eu/esco/occupation/0181835d-dd00-49e2-9fc4-e1382a65957f', 'insulation supervisor', 'supervisor of installation of insulating materials\nsupervisor of insulation materials installation\nsupervisor of installation of insulation\nsolid wall insulation installation supervisor\ninsulation installers supervisor\ncavity wall insulation installation supervisor\nloft insulation installation supervisor', '', 'released', '2016-07-05 16:01:57', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Insulation supervisors monitor insulation operations. They assign tasks and take quick decisions to resolve problems.', '3123.1.13'),
