(1543, 368, 'Occupation', 'http://data.europa.eu/esco/occupation/773753ad-c4f7-4052-b3b4-440435199d6b', 'cosmetics and perfume specialised seller', 'beauty and aroma salesperson\nbeauty and aroma specialised seller\nbeauty salesperson\nbeauty specialised seller\ncosmetics salesperson\nperfume specialised seller\ncosmetics specialised seller\ncosmetics and toiletries specialised seller\ncosmetics and toilet articles specialised seller\ncosmetics and toiletries salesperson\ncosmetics and perfume salesperson\ncosmetics and toilet articles salesperson\nmake-up and cosmetics specialised seller\nmake-up and cosmetics salesperson\nperfume salesperson', '', 'released', '2016-07-05 17:14:35', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Cosmetics and perfume specialised sellers sell cosmetics and toilet articles in specialised shops.', '5223.7.12'),
(1544, 368, 'Occupation', 'http://data.europa.eu/esco/occupation/80fe3002-3ae4-4d3b-8969-0f2ce1049d7d', 'jewellery and watches specialised seller', 'luxury watches and diamond jewellery specialised seller\ndiamonds and precious metal jewellery salesperson\ndiamonds and precious metal jewellery specialised seller\nluxury watches, diamonds and jewellery salesperson\njewellery specialised seller\ndiamond specialised seller\nluxury watches and diamond jewellery salesperson\njewellery salesperson\ndiamond salesperson\njewellery and watches salesperson', '', 'released', '2016-12-22 15:47:08', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Jewellery and watches specialised sellers sell, maintain and clean jewels and watches in specialised shops.', '5223.7.22'),
