(1931, 66, 'Occupation', 'http://data.europa.eu/esco/occupation/b0f3f595-4965-4f13-ba5c-f6049b62469f', 'biometrician', 'biometric studies analyst\nbiometrics researcher\nbiometric studies research scientist\nbiometric studies researcher\nbiometric studies scientist\nbiometricians\nbiometrics research scientist\nbiometrics scientist\nbiometrics analyst\nbiometric studies scholar\nbiometrics science researcher\nbiometric studies research analyst\nbiometrics research analyst\nbiometrics scholar', '', 'released', '2016-07-05 16:23:52', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Biometricians perform research in the field of biometrics. They carry statistical or biological research projects by measuring fingerprints, retinas, and human shapes for medical or industrial applications.', '2120.6.1'),
(1932, 68, 'Occupation', 'http://data.europa.eu/esco/occupation/a7a74a05-3dd0-46c6-99af-92df8042520c', 'microbiologist', 'microbiology studies research scientist\nmicrobiology research analyst\nmicrobiology studies analyst\nvirologist\nhistologist\nmicrobiology scholar\nmicrobiology research scientist\nmicrobiology researcher\nbacteriologist\nmicrobiology studies scientist\nmicrobiology analyst\nhelminthologist\nmicrobiology biotechnologist\nmicrobiology studies scholar\nmicrobiology scientist\nmicrobiology studies researcher\nparasitologist\nmicrobiology science researcher\nmicrobiology studies research analyst', '', 'released', '2017-01-17 14:18:17', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Microbiologists study and research the life forms, characteristics, and processes of microscopic organisms. They study microorganisms such as bacteria, protozoa, fungi, etc. in order to diagnose and counteract the effects that these microorganisms might have in animals, in the environment, in the food industry, or in the health care industry.', '2131.4.9'),
