(2378, 459, 'Occupation', 'http://data.europa.eu/esco/occupation/c0749528-ee2a-41bd-a1f6-c13825b5e569', 'roadside vehicle technician', 'van recovery technician\nheavy vehicle roadside technician\ncoach roadside technician\nPCV roadside technician\nbus and coach roadside technician\nLGV roadside technician\nautomotive recovery technician\ntruck recovery technician\nbus roadside technician\nvehicle recovery technician\nmobile auto mechanic\ncar recovery technician\nlight vehicle roadside technician\nmobile vehicle mechanic', '', 'released', '2017-01-17 12:14:16', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Roadside vehicle technicians perform onsite repairs, tests and maintenance to roadside vehicles. They locate and travel to customers\' vehicles to provide services such as tyre replacements and engine repairs.', '7231.6'),
(2379, 465, 'Occupation', 'http://data.europa.eu/esco/occupation/bca063d2-a904-45db-b3f4-0fd7e681753e', 'surgical instrument maker', 'orthopaedic appliance maker\nsurgical instrument designer\nmedical instrument design technician\nsurgical device repairer\nsurgical instrument assembler\nmedical instrument designer\nmedical instrument repairer\nsurgical device design technician\nmedical instrument assembler\nsurgical instrument technician\nsurgical instrument design technician\nmedical instrument technician\nsurgical device assembler\nsurgical instrument repairer\nsurgical device designer\nsurgical device technician', '', 'released', '2017-01-17 13:44:02', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Surgical instrument makers create, repair and design surgical instruments, such as clamps, graspers, mechanical cutters, scopes, probes and other surgical instruments.', '7311.5'),
(2380, 466, 'Occupation', 'http://data.europa.eu/esco/occupation/bd55e8d7-8bdf-4179-a84b-ed43073f05a1', 'harp maker', 'celtic harp builder\nlever harp maker\nwire harp builder\nmulti-course harp maker\nlever harp builder\npedal harp builder\nceltic harp maker\npedal harp maker\nwire harp maker\nmulti-course harp builder', '', 'released', '2016-07-05 17:06:30', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Harp makers create and assemble parts to create harps according to specified instructions or diagrams. They sand wood, measure and attach strings, test quality of strings and inspect the finished instrument.', '7312.6.2'),
(2381, 466, 'Occupation', 'http://data.europa.eu/esco/occupation/c644e6f6-dffc-4d7a-bf36-a384e465937b', 'keyboard musical instrument maker', 'keyboard musical instrument assembler\nkeyboard musical instrument constructor\nkeyboard musical instrument craftswoman\nkeyboard musical instrument production worker\nkeyboard musical instrument craftsman\nkeyboard musical instrument builder', '', 'released', '2016-07-05 17:03:04', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Keyboard musical instrument makers create and assemble parts to create keyboard instruments according to specified instructions or diagrams. They sand wood, tune, test and inspect the finished instrument.', '7312.3'),
(2382, 466, 'Occupation', 'http://data.europa.eu/esco/occupation/c7b31477-88e3-400f-988f-3baa090de173', 'organ builder', 'organ maker\npipe organ designer\npipe organ maker\norgan designer\npipe organ builder\norgan constructor\npipe organ constructor', '', 'released', '2016-07-05 17:05:58', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/member-occupations,\nhttp://data.europa.eu/esco/concept-scheme/occupations', 'Organ builders create and assemble parts to build organs according to specified instructions or diagrams. They sand wood, tune, test and inspect the finished instrument.', '7312.3.2'),
(2383, 469, 'Occupation', 'http://data.europa.eu/esco/occupation/c9f6c245-8c66-4b13-9253-9835194abb23', 'glass beveller', 'glass beveller machine operator\nglass hand-beveller\nglass beveling machine operator\nglass edger\nhand-beveller\nglass cutter\nglass finisher', '', 'released', '2016-07-05 17:05:13', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Glass bevellers measure, cut, assemble and install flat glass and mirrors. They also load and unload glass, mirrors and equipment, drive to installation sites, install metal or wood frameworks that need to be fitted with glass, and work according to client specifications.', '7315.2'),
(2384, 471, 'Occupation', 'http://data.europa.eu/esco/occupation/b9d03330-c71c-4827-8797-3e96249d54db', 'woodcarver', 'artisan woodworker\nheritage wood worker\nmaster woodcarver\ncraft wood carver\nheritage woodcarver\ntraditional woodcarver\nheritage woodworker\nartisan woodcarver\ntraditional wood carver\nartisan\nheritage wood carver\ntraditional handcarver\nartisan wood worker\ncraft woodcarver\nartisanal woodcarver\nhandcarver', '', 'released', '2016-07-05 16:56:46', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Woodcarvers manually shape wood into the desired shape using equipment like knives, gouges and chisels. Woodcarvers produce wooden products to serve as decoration, to be integrated into a composite product, as utensils or as toys.', '7317.6'),
(2385, 475, 'Occupation', 'http://data.europa.eu/esco/occupation/cea4d5c9-7805-434a-8e6b-af5e9fa54c9e', 'scanning operator', 'scanning machine operator\nscanning specialist\ndocument scanning specialist\ndocument scanner\nscanning clerk\nscanner operator\nscanner tender\nscanning machine tender\nscanner worker\ndocument scanning operator\nscanner technician\nscanning technician\nscanner specialist', '', 'released', '2016-10-20 16:43:22', 'http://data.europa.eu/esco/regulated-professions/unregulated', '', '', 'http://data.europa.eu/esco/concept-scheme/occupations,\nhttp://data.europa.eu/esco/concept-scheme/member-occupations', 'Scanning operators tend scanners. They feed print materials into the machine and set controls on the machine or on a controlling computer to obtain the highest resolution scan.', '7321.1.4'),
